/*******************************************************************************
* Function Name  : UARTSend
* Description    : Send a string to the UART.
* Input          : - pucBuffer: buffers to be printed.
*                : - ulCount  : buffer's length
* Output         : None
* Return         : None
*******************************************************************************/
void UARTSend(char *buffer){
	while(*buffer)// Loop while there are more characters to send.
	{
		USART_SendData(USART2, *buffer++);//
		while(USART_GetFlagStatus(USART2, USART_FLAG_TC) == RESET); //wait for next character to send
	}
}
void clear(char *str){
	while(*str){
		*str++ = '\0';
	}
}

void strcopy(char* str1, char *str2){
	while(*str1){
		*str2 = *str1++;
		str2++;
	}
}

void delay(long cycles){
	while(cycles--);
}
uint16_t getVal(char *recBuffer){
	uint8_t i=0;
	for(i=0; i<NUM; i++){
		if(recBuffer[i] == ',')
			continue;
		else {
			return recBuffer[i];
		}
	}
	return 0;
}
